/**
 * LaunchPad for EE
 *
 * @package		Launchpad
 * @subpackage	ThirdParty
 * @category	Modules
 * @author		Bjørn Børresen
 * @link		http://ee.bybjorn.com/launchpad/
 */
class Launchpad_upd {
		
	var $version        = '1.0'; 
	var $module_name = "Launchpad";

    // global variables will be populated on install and deleted on uninstall
    private $global_vars = array(
        'launchpad_title' => '<span>You&rsquo;ve found</span> {site_name}',
        'launchpad_body' => 'We&rsquo;re not <em>quite</em> there yet&mdash;<em>but we&rsquo;re getting there!</em>&mdash;and we <strong>really</strong> want you to know when we&rsquo;re ready. Here&rsquo;s how to stay updated:',
        'launchpad_rss_feed' => 'http://feeds.feedburner.com/addonbakery',
        'launchpad_use_feedburner_for_mail' => 'n',
        'launchpad_be_nice' => 'y',
    );
	
    function Launchpad_upd( $switch = TRUE ) 
    { 
		// Make a local reference to the ExpressionEngine super object
		$this->EE =& get_instance();
    } 

    /**
     * Installer for the Launchpad module
     */
    function install() 
	{				
						
		$data = array(
			'module_name' 	 => $this->module_name,
			'module_version' => $this->version,
			'has_cp_backend' => 'y'
		);

		$this->EE->db->insert('modules', $data);		
		$site_id = $this->EE->config->item('site_id');
        foreach($this->global_vars as $key => $value)
        {
            $this->EE->db->insert('snippets',
                array(
                    'snippet_name' => $key,
                    'snippet_contents' => $value,
                    'site_id' => $site_id,
                ));
        }
																									
		return TRUE;
	}

	
	/**
	 * Uninstall the Launchpad module
	 */
	function uninstall() 
	{ 				
		
		$this->EE->db->select('module_id');
		$query = $this->EE->db->get_where('modules', array('module_name' => $this->module_name));
		
		$this->EE->db->where('module_id', $query->row('module_id'));
		$this->EE->db->delete('module_member_groups');
		
		$this->EE->db->where('module_name', $this->module_name);
		$this->EE->db->delete('modules');
		
		$this->EE->db->where('class', $this->module_name);
		$this->EE->db->delete('actions');
		
		$this->EE->db->where('class', $this->module_name.'_mcp');
		$this->EE->db->delete('actions');

        $site_id = $this->EE->config->item('site_id');
        foreach($this->global_vars as $key => $value)
        {
            $this->EE->db->delete('snippets', array('snippet_name' => $key, 'site_id' => $site_id ));
        }

										
		return TRUE;
	}
	
	/**
	 * Update the Launchpad module
	 * 
	 * @param $current current version number
	 * @return boolean indicating whether or not the module was updated 
	 */
	
	function update($current = '')
	{
		return FALSE;
	}

    /** @return Devkit_code_completion_helper */ function EE() {if(!isset($this->EE)){$this->EE =& get_instance();}return $this->EE;}    
}

